class Person {
  constructor(name) {
    this.name = name;
  }

  greet() {
    return 'Hello, ' + this.name;
  }
}

class Employee extends Person {
  constructor(name, salary) {
    super(name);
    this.salary = salary;
  }

  grantRaise(percent) {
    percent /= 100;
    this.salary += (this.salary * percent).toInt();
  }
}

