class Person {
  name : string;

  constructor(name) {
    this.name = name;
  }

  greet() {
    return 'Hello, ' + this.name;
  }
}

class Employee extends Person {

  salary : number;

  constructor(name : string, salary : number) {
    super(name);
    this.salary = salary;
  }

  grantRaise(percent : number) {
    percent /= 100;
    this.salary += (this.salary * percent).toInt();
  }
}

