function measure(fn) {
  var start = Date.now();
  fn();
  return Date.now() - start;
}

var client = new XMLHttpRequest;

client.onreadystatechange = function() {  
  if (this.readyState == 4) {  
    processData(this);
  }
}
client.open('GET', 'data.json');
client.send();

